mapper.update:
       Update sbCustomer
      		Set
 				sbPhoneNumber = ifnull(#{sbPhoneNumber},'01')
           	,sbPw = #{sbPw}
           	,updateDateTime = now()
		Where customerKey = #{customerKey}

mapper.insert:

		 INSERT INTO sbCustomer( 
        	 sbPhoneNumber
        	,createDateTime 
        	,token
            ,tokenDateTime
            )
        VALUES (
	  		  #{sbPhoneNumber}
	  		, now()
	  		, #{token}
	  		, now()
	  		)      
	  	ON DUPLICATE KEY UPDATE token = #{token}
            	               ,tokenDateTime = now()

mapper.select:
	SELECT T1.token
  		FROM sbCustomer T1
  	WHERE T1.sbPhoneNumber = #{sbPhoneNumber}
