mapper.select:
	SELECT count(*) AS TOT_COUNT
  		FROM smAs T1
         WHERE T1.customerKey = #{customerKey}
         AND T1.groupId= #{groupId}

mapper.update:
       Update smAs
      		Set
      		   asState =#{asState}
 			   ,asSubState =#{asSubState}
		   Where asNo = #{asNo}

mapper.insert:
  	  	Insert into smAsHistory( 
        	 asNo
        	,userId
        	,groupId
        	,asState
        	,asSubState
        	,asHistory
            ,asHistoryDateTime
            )
        Values (
              #{asNo}
            , #{userId}
            , #{groupId}
            , #{asState}
            , #{asSubState}
            , #{asHistory}
            , now()
	  		)
