mapper.insert:

  	Insert into smCounsel( 
        	 customerKey
            ,groupId
            ,customerId
            ,counselState 
            ,counsel
            ,counselImage
            ,counselDateTime
            )
        Values (
	  		  #{customerKey}
	  		, #{groupId}
	  		, #{customerId}
	  		, #{counselState}
	  		, #{counsel}
	  		, #{counselImage}
	  		, now()
	  		)

mapper.select:
	SELECT count(*) AS TOT_COUNT
  		FROM smCounsel T1
         WHERE T1.groupId= #{groupId} 
               And T1.customerKey = #{customerKey}
